#! /bin/bash
package=podman

echo "[TARGET PACKAGE]........................: $package"
echo "[VERIFY REPO SSL]........................: $sslverify"

echo "...........[START: CREATING REPO]..........."
echo '' | base64 -d > /etc/yum.repos.d/redhat-cloud.repo
echo "...........[END: CREATING REPO]..........."

echo "...........[START: CREATING CERTIFICATES]..........."
mkdir -p /etc/pki/rhui/product
echo '' | base64 -d > /etc/pki/rhui/ca.crt
echo ''  | base64 -d > /etc/pki/rhui/key.pem
echo ''  | base64 -d > /etc/pki/rhui/product/content.crt
echo "...........[END: CREATING CERTIFICATES]..........."

echo "...........[START: DOWNLOAD ONLY INSTALL PACKAGE]..........."
mkdir -p /var/tmp
yum install --downloadonly --downloaddir=/var/tmp/$package $package -y
echo "...........[END: DOWNLOAD ONLY INSTALL PACKAGE]..........."
echo "...........[START: INSTALL PACKAGE CREATEREPO AND TAR PACK]..........."
yum install createrepo -y
mkdir -p /var/tmp/podman
createrepo --database /var/tmp/$package
tar cvf podman.tar /var/tmp/$package
echo "...........[END: INSTALL PACKAGE CREATEREPO AND TAR PACK]..........."

#! /bin/bash
package=podman

echo "[TARGET PACKAGE]........................: $package"

echo "...........[START: EXTRACT TAR PACK]..........."
mkdir -p /var/tmp/$package
tar -xvf podman.tar -C /
echo "...........[END: EXTRACT TAR PACK]..........."

echo "...........[START: CREATE REPO TO FILE]..........."
echo "[offline-$package]" > "/etc/yum.repos.d/$package.repo"
echo "name=$package-offline" >> "/etc/yum.repos.d/$package.repo"
echo "baseurl=file:///var/tmp/$package" >> "/etc/yum.repos.d/$package.repo"
echo "enabled=1" >> "/etc/yum.repos.d/$package.repo"
echo "gpgcheck=0" >> "/etc/yum.repos.d/$package.repo"
echo "...........[END: CREATE REPO TO FILE]..........."

echo "...........[START: INSTALL PACKAGE]..........."
yum install podman -y
echo "...........[END: INSTALL PACKAGE]..........."


